// Pick the image and colour from the FontAwesome collection: https://fontawesome.com/icons/image?f=classic&s=solid&pc=%2325bcef&sc=%2325bcef
//% color=#25bcef icon="\uf03e" block="OLED obrázky"
namespace OledImage {

    let debugMode = false

    export enum Type {
        //% blockId="happy_face" block="Veselá tvář"
        HappyFace,
        //% blockId="sad_face" block="Smutná tvář"
        SadFace,
        //% blockId="crying_face" block="Plačící tvář"
        CryingFace,
        //% blockId="frowning_face" block="Mračící se tvář"
        FrowningFace,
        //% blockId="sleeping_face" block="Spící tvář"
        SleepingFace,
    }

    //% blockId=debug_mode block="ladící režim zapnut: %enabled"
    export function setDebugMode(enabled: boolean) {
        debugMode = enabled
    }

    /**
     * Draw an image at a position
     * ```
     * images.createImage(`
     *     . # . # .
     *     # . . . #
     *     . . # . .
     *     # . . . #
     *     . # . # .
     * `)
     * ```
     * @param x X coordinate
     * @param y Y coordinate
     * @param image The image to be drawn
     */
    //% blockId=draw_image_at_position block="na pozici %x:%y vykresli obrázek %Type"
    //% x.min=0 x.max=127
    //% y.min=0 y.max=127
    export function drawImageAt(x: number, y: number, image: Image) {
        oled.drawImage(image, x, y, true, true, false)
    }

    /**
     * Draw an image at the 0:0 position
     * @param imageType The image to be drawn
     */
    //% blockId=draw_image block="vykresli obrázek %Type"
    export function drawImage(imageType: Type) {
        switch (imageType) {
            case Type.HappyFace:
                drawImageAt(15, 7, openedEye16x16)
                drawImageAt(DISPLAY_WIDTH - 32, 7, openedEye16x16)
                drawImageAt((DISPLAY_WIDTH - 64) / 2, DISPLAY_HEIGHT - 16, smilingMouth64x16)
                break
            case Type.SadFace:
                drawImageAt(15, 7, sadEye16x16)
                drawImageAt(DISPLAY_WIDTH - 32, 7, sadEye16x16)
                drawImageAt((DISPLAY_WIDTH - 64 - 1) / 2, DISPLAY_HEIGHT - 16, sadMouth64x16)
                break
            case Type.CryingFace:
                drawImageAt(15, 7, sadEye16x16)
                drawImageAt(DISPLAY_WIDTH - 32, 7, sadEye16x16)
                drawImageAt(DISPLAY_WIDTH - 16, 32, tear8x8)
                drawImageAt((DISPLAY_WIDTH - 64) / 2, DISPLAY_HEIGHT - 16, sadMouth64x16)
                break
            case Type.FrowningFace:
                drawImageAt(15, 7, frowningLeftEye16x16)
                drawImageAt(DISPLAY_WIDTH - 32, 7, frowningRightEye16x16)
                drawImageAt((DISPLAY_WIDTH - 16) / 2, (DISPLAY_HEIGHT - 16) / 2, openedEye16x16) // Nose
                drawImageAt((DISPLAY_WIDTH - 64) / 2, DISPLAY_HEIGHT - 16, normalMouth64x16)
                break
            case Type.SleepingFace:
                drawImageAt(15, 7, sleepingEye16x16)
                drawImageAt(DISPLAY_WIDTH - 32, 7, sleepingEye16x16)
                drawImageAt((DISPLAY_WIDTH - 64) / 2, DISPLAY_HEIGHT - 16, sleepingMouth64x16)
                break
            default:
                drawImageAt((DISPLAY_WIDTH - 32) / 2, (DISPLAY_HEIGHT - 32) / 2, unknownImageType32x32)
        }

        if (debugMode) oled.drawImage(debugMatrix128x64, 0, 0, true, false, false)
        oled.draw()
    }
}

